import React, {Component} from "react";
import firebase from "firebase/app";
import "./App.css";

const firebaseConfig = {
  apiKey: "AIzaSyBjonRNEb0SYCueDyQzRZ0JoInBz6AmXII",
  authDomain: "computational-game.firebaseapp.com",
  databaseURL: "https://computational-game-default-rtdb.firebaseio.com",
  projectId: "computational-game",
  storageBucket: "computational-game.appspot.com",
  messagingSenderId: "877910975145",
  appId: "1:877910975145:web:31b4151f82274c97abffb8",
};

if (!firebase.apps.length) {
  firebase.initializeApp(firebaseConfig);
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      num: 0,
      msg: "count start",
      all: [],
      ans: 0,
      data: 4,
    };
    this.doAction = this.doAction.bind(this);
    this.showAns = this.showAns.bind(this);
    this.handleLevelChange = this.handleLevelChange.bind(this);
  }

  doAction(e) {
    this.state.all.splice(0, this.state.all.length);
    document.getElementById("on_off").disabled = "true";
    document.getElementById("on_off2").disabled = "true";

    this.intervalid = setInterval(() => {
      this.setState((state) => ({
        num: Math.floor(Math.random() * 90) + 10,
        msg: this.state.counter,
        all: this.state.all.concat(this.state.num),
        ans: 0,
        lev: 1,
      }));
      if (this.state.all.length === 10) {
        clearInterval(this.intervalid);
        document.getElementById("on_off").disabled = "";
        document.getElementById("on_off2").disabled = "";
      }
    }, Math.ceil(1600 / this.state.lev));
  }

  Cal_ans_num() {
    this.state.all.push(this.state.num);
    let arr = this.state.all;
    let sum = function (arr) {
      return arr.reduce(function (prev, current, i, arr) {
        return prev + current;
      });
    };
    return sum(arr);
  }

  showAns(e) {
    if (this.state.all.length > 0) {
      this.setState((state) => ({
        ans: this.Cal_ans_num(),
        num: 0,
      }));
    } else {
      this.setState((state) => ({ans: 0}));
    }
  }

  getFireData() {
    let db = firebase.database();
    let ref = db.ref("sample");
    ref.on("value", (snapshot) => {
      this.setState({
        data: snapshot.child("1").child("ans").val(),
      });
    });
  }

  handleLevelChange(e){
    console.log(`LEVEL${e.target.value} が選択されました`)
    this.setState((state) => ({
      ...state,
      lev: e.target.value
    }))
  }

  render() {
    return (
      <div className="First">
        <div className="App-header">
          <p className="Num_Display">{this.state.num}</p>
          <div className="Second">
            <div>
              <select className="Speed_Button" defaultValue={"1"} id="target" value={this.state.lev} onChange={this.handleLevelChange}>
                <option value="1" disabled>
                  Choose Game Level
                </option>
                <option value="2">Speed Lv.1</option>
                <option value="3">Speed Lv.2</option>
                <option value="4">Speed Lv.3</option>
              </select>
            </div>
          </div>

          <div className="Third">
            <button className="Click_Box" onClick={this.doAction} id="on_off" disabled={this.state.lev === 1}>
              Game Start!!
            </button>
          </div>

          <div className="Fourth">
            <button className="ans_button" onClick={this.showAns} id="on_off2" disabled={this.state.all.length !== 10}>
              SHOW ANSWER
            </button>
            <br />
          </div>

          <div className="Fifth">
            <table className="res">
              <tbody>
                <tr>
                  <td>{this.state.ans}</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
